<script>
import { reactive, ref , computed } from 'vue'
import { useStore } from 'vuex'
import { useRouter } from 'vue-router'
import QrcodeVue from 'qrcode.vue'
import Vue3Barcode from 'vue3-barcode'
import { Icon } from '@vicons/utils'
import { useDialog, useMessage, useNotification } from 'naive-ui'
import soundUrl from './../../../../assets/sounds/mouseclick.mp3'

export default {
  name: "PhonicPhase1" ,
  components: {
    QrcodeVue ,
    Vue3Barcode,
    Icon
  },
  setup(){
    var store = useStore()
    const dialog = useDialog()
    const message = useMessage()
    const notify = useNotification()

    /**
     * Variables
     */    
    var model = reactive( {
      name: "PhonicPhase1" ,
      title: "សម្លេងព្យញ្ជនៈវគ្គ១"
    })

    const visiblePhonic = ref(null)
    const phonics = ref([ "s" , "a" , "t" , "p" , "i" , "n" , "m" , "d" , "g" , "o" , "c" , "k" , "ck" , "e" , "u" , "r" , "h" , "b" , "f" , "ff" , "l" , "ll" , "ss" ])


    function updatePhonic(){
      visiblePhonic.value = phonics.value[  Math.floor(Math.random() * phonics.value.length ) ]
    }

    setInterval( updatePhonic , 6000 )

    function playSound(){
      console.log( soundUrl )
      var audio = new Audio(soundUrl)
      audio.play()
    }

    return {
      /**
       * Variables
       */
      model ,
      visiblePhonic ,
      phonics ,
      /**
       * Functions
       */
      updatePhonic ,
      playSound
    }
  }
}

</script>

